mapper.update:
update digital_shop
nickname = #{nickname}, avatar = #{avatar}, password = #{password}, phone = #{phone}, email = #{email}, province_code=#{provinceCode}, city_code=#{cityCode}, district_code=#{districtCode}, detail_address=#{detailAddress}, introduce = #{introduce}, practice_license = #{practiceLicense}, principal_name = #{principalName}, principal_no = #{principalNo}, audit_status = #{auditStatus},
where id = #{id}

mapper.select:
select id, nickname, avatar, phone, province_code, city_code,
district_code, detail_address from digital_shop

    and nickname like concat('%', #{nickname}, '%')
    and province_code = #{provinceCode}
    and city_code = #{cityCode}
    and district_code = #{districtCode}
    and audit_status = 2
    and is_deleted = 0

order by create_time
