mapper.update:
update user
set balance = balance + #{money}
where id = #{id}

update user
set balance = balance - #{money}
where id = #{id}

mapper.select:
select balance
from user
where id = #{id}

mapper.update:
update user
nickname = #{nickname}, avatar = #{avatar}, password = #{password}, phone = #{phone}, email = #{email}, balance = #{balance},
where id = #{id}
